mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId}, update_time=#{updateTime}
where id = #{id}

mapper.select:
select s.*, c.name clazzName from student s left join clazz c on s.clazz_id=c.id


        and s.name like concat('%',#{name},'%')


        and s.degree=#{degree}


        and s.clazz_id=#{clazzId}

SELECT c.name clazz , COUNT(*) AS data
FROM student s left join clazz c on c.id = s.clazz_id
GROUP BY s.clazz_id order by data;
